StoredProcedureSqlServerv2k5.body:
@NWAID int, @AccessionDate date.me, @UserLogin varchar(15) AS
IF EXISTS (SELECT 1 FROM OFFICER_ACCESSION WHERE NWA_ID = @NWAID) BEGIN --- UPDATE ACCESSION DATA UPDATE OFFICER_ACCESSION SET ACCESSION_DATE = @AccessionDate, USER_LOGIN = @UserLogin, UPDATE_DATE = GETDATE() WHERE NWA_ID = @NWAID
END ELSE BEGIN --- INSERT ACCESSION DATA INSERT INTO OFFICER_ACCESSION (NWA_ID, ACCESSION_DATE, USER_LOGIN, UPDATE_DATE) VALUES (@NWAID, @AccessionDate, @UserLogin, GETDATE()) END
