StoredProcedureSqlServerv2k5.body:
@ZipCode varchar(9), 
 @OrgId int, 
 @UserOrgId int, 
 @UserLogin varchar(15) 

AS 

SET NOCOUNT ON 

UPDATE MUD_FILE 
SET ASSIGNED_ORG_ID = @OrgId, 
 USER_LOGIN = @UserLogin, 
 UPDATE_DATE = getdate() 
WHERE ZIP = @ZipCode 


--- UPDATE THE HISTORY TABLE WITH LATEST DATE 
EXEC sp_UpdateMudHistoryLog @UserOrgId, @UserLogin
