StoredProcedureSqlServerv2k5.body:
@ContractID int, @PADD date.me, @UserLogin varchar(15) AS
UPDATE CONTRACT SET PROJECTED_SHIP_DATE = @PADD, USER_LOGIN = @UserLogin, UPDATE_DATE = GetDate() WHERE CONTRACT_ID = @ContractID
INSERT INTO PADD_HISTORY (CONTRACT_ID, PROJECTED_SHIP_DATE, PADD_CHANGED_DATE, USER_LOGIN, UPDATE_DATE) VALUES (@ContractID, @PADD, GetDate(), @UserLogin, GetDate())
