StoredProcedureSqlServerv2k5.body:
@NROTCLeaveOfAbsenceID int = 0, @NWAID int, @ContractID int = NULL, @LeaveOfAbsenceReason int, @LeaveOfAbsenceDate date.me, @UserLogin varchar(15) AS
IF EXISTS (SELECT 1 FROM NROTC_LEAVE_OF_ABSENCE WHERE NROTC_LEAVE_OF_ABSENCE_ID = @NROTCLeaveOfAbsenceID) BEGIN UPDATE NROTC_LEAVE_OF_ABSENCE SET NWA_ID = @NWAID, CONTRACT_ID = @ContractID, LEAVE_OF_ABSENCE_REASON = @LeaveOfAbsenceReason, LEAVE_OF_ABSENCE_DATE = @LeaveOfAbsenceDate, USER_LOGIN = @UserLogin, UPDATE_DATE = GETDATE() WHERE NROTC_LEAVE_OF_ABSENCE_ID = @NROTCLeaveOfAbsenceID
END ELSE BEGIN INSERT INTO NROTC_LEAVE_OF_ABSENCE (NWA_ID, CONTRACT_ID, LEAVE_OF_ABSENCE_REASON, LEAVE_OF_ABSENCE_DATE, USER_LOGIN, UPDATE_DATE) VALUES (@NWAID, @ContractID, @LeaveOfAbsenceReason, @LeaveOfAbsenceDate, @UserLogin, GETDATE()) END
