StoredProcedureSqlServerv2k5.body:
@Transac.onID int, @Transac.onTypeID int, @ScheduleID int, @Exported char(1), @UserLogin varchar(15) AS
IF EXISTS (SELECT 1 FROM MIRS_EXPORT WHERE TRANSACTION_ID = @Transac.onID) BEGIN UPDATE MIRS_EXPORT SET EXPORTED = @Exported, USER_LOGIN = @UserLogin, UPDATE_DATE = GETDATE() WHERE TRANSACTION_ID = @Transac.onID
--- FOR RECORDS WITH A @Transac.onTypeID = 1 (NEW SCHEDULE RECORDS) --- SET PROCESSING_STATUS TO 'M' - PROJECTED TO MIRS IF @Transac.onTypeID = 1 AND EXISTS (SELECT 1 FROM MEPS_SCHEDULE WHERE SCHEDULE_ID = @ScheduleID) BEGIN UPDATE MEPS_SCHEDULE SET PROCESSING_STATUS = 'M', USER_LOGIN = @UserLogin, UPDATE_DATE = GETDATE() WHERE SCHEDULE_ID = @ScheduleID END END
