StoredProcedureSqlServerv2k5.body:
@OCSID int, @NoShow char(1) = NULL, @ReportDate date.me = NULL, @UserLogin varchar(15) AS
IF EXISTS (SELECT 1 FROM OCS WHERE OCS_ID = @OCSID) BEGIN UPDATE OCS SET CLASS_RESULT_CODE = CASE @NoShow WHEN 'Y' THEN 'O' ELSE CLASS_RESULT_CODE END, REPORT_DATE = CASE WHEN COALESCE(@NoShow, 'N') = 'N' THEN @ReportDate ELSE REPORT_DATE END WHERE OCS_ID = @OCSID
END
