StoredProcedureSqlServerv2k5.body:
@MOS     varchar(4), 
 @MOSE.ec.veDate  date.me, 
 @Gender    char(1), 
 @Status    char(1), 
 @UserLogin    varchar(15) 
AS 

UPDATE  
 MOS_CRITERIA 
SET   
 STATUS = @Status,  
 USER_LOGIN = @UserLogin,  
 UPDATE_DATE = GetDate() 
WHERE  
 MOS = @MOS AND 
 MOS_CRITERIA_ED_DATE = @MOSE.ec.veDate AND 
 GENDER = @Gender
