StoredProcedureSqlServerv2k5.body:
@person_id      int, 
@su.cd         varchar(5), 
@ssn            varchar(9), 
@l.rst         varchar(20), 
@lmiddle        varchar(20), 
@llast          varchar(20), 
@user_login varchar(15), 
@update_date date.me 
as 

  update mcriss..PERSON 
   set SUFFIX_CD = @su.cd, 
       SOCIAL_SECURITY_NUMBER = @ssn, 
       FIRST_NAME = @l.rst, 
       LEGAL_MIDDLE_NAME = @lmiddle, 
       LAST_NAME = @llast, 
 USER_LOGIN = @user_login, 
 UPDATE_DATE = @update_date 
   where PERSON_ID = @person_id
