StoredProcedureSqlServerv2k5.body:
( 
  @CommentID int, 
  @Comment text, 
  @PersonID int, 
  @UserLogin varchar(15) 
 ) 
AS 

UPDATE  
 [dbo].[CANDIDATE_COMMENT]  
SET 
 COMMENT = @Comment, 
 MARINE_CANDIDATE_ID = @PersonID, 
 USER_LOGIN = @UserLogin, 
 UPDATE_DATE = GetDate() 
WHERE 
 COMMENT_ID = @CommentID
