StoredProcedureSqlServerv2k5.body:
@MOS     varchar(4), 
 @MOSE.ec.veDate  date.me, 
 @Gender    char(1), 
 @Status    char(1), 
 @LastEditBy   varchar(15) 
AS 

UPDATE  
 [dbo].[MOS_CRITERIA] 
SET   
 STATUS = @Status,  
 USER_LOGIN = @LastEditBy,  
 UPDATE_DATE = GetDate() 
WHERE  
 MOS = @MOS AND 
 MOS_CRITERIA_ED_DATE = @MOSE.ec.veDate AND 
 GENDER = @Gender
