StoredProcedureSqlServerv2k5.body:
( 
 @ID int, 
 @Comments varchar(40), 
 @E.ec.veDate date.me, 
 @NwaID int, 
 @PhaseCode varchar(10), 
 @Disposi.onCode varchar(20), 
 @Usage char(1), 
 @LastEditBy varchar(15) 
) 
AS 

UPDATE [mcriss].[dbo].[CANDIDATE_STATUS] SET 
 [NWA_ID] = @NwaID 
 ,[STATUS_EFFECTIVE_DATE] = @E.ec.veDate 
 ,[COMMENTS] = @Comments 
 ,[STATUS_TYPE] = @Usage 
 ,[STATUS_CODE] = @PhaseCode 
 ,[DISPOSITION_CODE] = @Disposi.onCode 
 ,[USER_LOGIN] = @LastEditBy 
 ,[UPDATE_DATE] = GetDate() 
WHERE [CANDIDATE_STATUS_ID] = @ID
