StoredProcedureSqlServerv2k5.body:
@Recrui.ngOrgID int, @CAIAc.vityDateBegin date.me, @CAIAc.vityDateEnd date.me AS SET NOCOUNT ON
SELECT ORGANIZATION_ID as Recrui.ngOrgID, CAI_ACTIVITY_DATE as CAIAc.vityDate, CAI_ACTIVITY_TYPE_ID as CAIAc.vityTypeID FROM [dbo].[CAI_ACTIVITY] WITH (NOLOCK) WHERE ORGANIZATION_ID = @Recrui.ngOrgID AND DATEDIFF(DAY, @CAIAc.vityDateBegin, CAI_ACTIVITY_DATE) >= 0 AND DATEDIFF(DAY, CAI_ACTIVITY_DATE, @CAIAc.vityDateEnd) >= 0 ORDER BY CAI_ACTIVITY_DATE ASC, CAI_ACTIVITY_TYPE_ID ASC
SET NOCOUNT OFF
