StoredProcedureSqlServerv2k5.body:
@orgID int, @dateFrom date.me = null, @dateTo date.me = null AS BEGIN
SELECT IRTCardID, RssOrgID, DatePrepared, Sector, Frequency, ForFY, RecruiterID, RecruiterRankName, Hours, LastUpdateDate, LastUpdateUser, SectorID, CardNameDesc, 1 AS NextFyCard FROM RSSOSS.USMC.IRTCard WHERE (RssOrgID = @orgID) AND (LEFT(DatePrepared,11) >= Coalesce(@dateFrom, LEFT(DatePrepared,11))) AND (LEFT(DatePrepared,11) <= Coalesce(@dateTo, LEFT(DatePrepared,11))) ORDER BY DatePrepared DESC
END
