StoredProcedureSqlServerv2k5.body:
@cardID uniqueidentifier AS BEGIN
SELECT irt.IRTCardID, irt.RssOrgID, irt.DatePrepared, irt.Sector, irt.Frequency, irt.ForFY, irt.RecruiterID, irt.RecruiterRankName, irt.Hours, irt.LastUpdateDate, irt.LastUpdateUser, irt.SectorID, irt.CardNameDesc, CASE WHEN irt2.IrtCardID IS NULL THEN 0 ELSE 1 END AS NextFyCard FROM IRTCard AS irt LEFT OUTER JOIN IRTCard AS irt2 ON irt.Sector = irt2.Sector AND (irt2.ForFY = (irt.ForFY + 1)) WHERE irt.IRTCardID = @cardID
END
