StoredProcedureSqlServerv2k5.body:
@TrainingFileId uniqueidentifier, @StartDate date.me = NULL AS
BEGIN
SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED
DECLARE @EndDate date.me;
IF @StartDate = NULL BEGIN SET @StartDate = GETDATE(); END
SET @EndDate = DATEADD(month, 8, @StartDate);
SELECT s.Date, s.Mission FROM Mul.MonthSummary mms, MonthMissionSummary s WHERE mms.ID=s.Mul.MonthSummaryID AND mms.TrainingFileID=@TrainingFileId AND s.Date BETWEEN @StartDate AND @EndDate ORDER BY s.Date
END
