StoredProcedureSqlServerv2k5.body:
@ProspectID uniqueidentifier AS BEGIN
SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;
SELECT ProspectSchoolId, ProspectId, SchoolId, LastUpdateUserId, Major, Gradua.onDate, IsFullTimeStudent, CurrentGpa, Cumula.veGpa, Educa.onLevel, Educa.onCode, ProjectedCompletedDate, ProspectSchoolTypeId FROM ProspectSchool
WHERE ProspectId = @ProspectID AND ProspectSchoolTypeId = 2 END
