StoredProcedureSqlServerv2k5.body:
( 
      @NwaID INT, 
      @MepsOrganiza.onID INT, 
      @ProcessingDate DATETIME, 
      @ArrivalDate DATETIME 
     ) 
AS 
   Select ISNULL(Schedule_ID, 0) 
   FROM [dbo].[MEPS_SCHEDULE] 
   WHERE [NWA_ID] = @NwaID AND  [MEPS_ORGANIZATION_ID] =  @MepsOrganiza.onID AND [PROCESSING_DATE] = @ProcessingDate AND [ARRIVAL_DATE] = @ArrivalDate AND [PROJECTION_TYPE_ID] = 5 -- 5 is P projec.on
