StoredProcedureSqlServerv2k5.body:
@Organiza.onID as integer, @SchoolYear as integer, @PlanType as varchar(40), @Objec.veMonth as integer AS
SELECT PRIORITY_CODE, OBJECTIVE_MONTH, OBJECTIVE_PERCENTAGE FROM HIGH_SCHOOL_MILESTONE WITH (NOLOCK) WHERE PLAN_TYPE = @PlanType AND ORGANIZATION_ID = @Organiza.onID AND SCHOOL_YEAR = @SchoolYear AND Month(OBJECTIVE_MONTH) = @Objec.veMonth AND ( (@Objec.veMonth = 6 AND Year(OBJECTIVE_MONTH) = @SchoolYear) OR (@Objec.veMonth <> 6) )
ORDER BY HIGH_SCHOOL_MILESTONE.PRIORITY_CODE
