StoredProcedureSqlServerv2k5.body:
@SchoolCode uniqueiden..er, @FiscalYear int AS SET NOCOUNT ON BEGIN
Select COUNT(*) NumCommissions from OFFICER_CONTRACT OFC inner join NWA N ON N.NWA_ID = OFC.NWA_ID inner join [CANDIDATE_STATUS] CS ON CS.NWA_ID = N.NWA_ID inner join CANDIDATE_EDUCATION CE ON N.MARINE_CANDIDATE_ID = CE.MARINE_CANDIDATE_ID AND CE.EDUCATIONTYPE = 1 inner join HIGH_SCHOOL HS ON HS.SCHOOL_CODE = CE.SCHOOL_CODE inner join OCS O ON O.NWA_ID = N.NWA_ID inner join OCS_CLASS OC ON OC.CLASS_ID = O.OCS_CLASS_ID where DISCHARGE_CODE is null AND hs.rowguid = @SchoolCode AND OC.FISCAL_YEAR = @FiscalYear AND CS.STATUS_CODE = 'D' AND CS.DISPOSITION_CODE = 'C' END
--- CLEAN UP SET NOCOUNT OFF
