StoredProcedureSqlServerv2k5.body:
@MOSCode varchar(4), @E.ec.veDate date.me, @Gender char(1) AS
SELECT MOS AS MOSCode, MOS_CRITERIA_ED_DATE As E.ec.veDate, GENDER, TERM_OF_ENLISTMENT, CITIZENSHIP, DRIVER_LICENSE, HIGHEST_WAIVER_LEVEL, HEIGHT_MINIMUM, HEIGHT_MAXIMUM, EDUCATION_TIER, AGE_MINIMUM, AGE_MAXIMUM, IsNull(VISION_LEFT_UNCORRECTED, '') As VisLe.Uncorr, IsNull(VISION_RIGHT_UNCORRECTED, '') As VisRightUncorr, IsNull(VISION_LEFT_CORRECTED, '') As VisLe.Corr, IsNull(VISION_RIGHT_CORRECTED, '') As VisRightCorr, AFQT, GT, EL, PAOI, APT, EDPT, MM, WPMT, DLAB, CL, MEOP, CYBER, IsNull(COLOR_PERCEPTION, '') As ColorPercep.on
FROM [dbo].[MOS_CRITERIA] WITH (NOLOCK) WHERE MOS = @MOSCode AND MOS_CRITERIA_ED_DATE = @E.ec.veDate AND GENDER = @Gender
