StoredProcedureSqlServerv2k5.body:
@ContractID int AS
SELECT MISSING_DOC_ID, CONTRACT_ID, COALESCE(CONVERT(CHAR(11), DATE_ENTERED, 106), null) AS DATE_ENTERED, COMMENTS, LEVEL, ASSIGNED, COALESCE(CONVERT(CHAR(11), DUE_DATE, 106), null) AS DUE_DATE, STATUS, USER_LOGIN, COALESCE(CONVERT(CHAR(11), UPDATE_DATE, 106), null) AS UPDATE_DATE
FROM MISSING_DOCUMENTS WITH (NOLOCK) WHERE CONTRACT_ID=@ContractID
