StoredProcedureSqlServerv2k5.body:
@EventId    uniqueidentifier, 
 @Organiza.onId   int, 
 @EventSourceId   int, 
 @EventTypeId   int, 
 @Loca.on    varchar(100), 
 @Comments    varchar(500), 
 @IsComplete             bit, 
 @StartDate              date.me, 
 @EndDate    date.me, 
 @PrecisionType   int  = null, 
 @EventTypeOther   varchar(50) = null, 
 @LastUpdatedBy   uniqueidentifier, 
 @Remarks    varchar(1000) = null, 
 @PgSentRefId            uniqueidentifier = null, 
 @SeriesID    int = 0 


AS 
BEGIN 

SET NOCOUNT ON 

-- STEP 2 & 3: Issue the data change statement(s), checking @@ERROR a.er each statement 
IF EXISTS(SELECT ID FROM [RSSOSS].[USMC].[Event] WHERE ID = @EventId) 
BEGIN 
 -- if @intCount > 0, then the Record already exists.  UPDATE. 
 UPDATE [RSSOSS].[USMC].[Event] 
   SET EventSourceId = @EventSourceId, 
       EventTypeId = @EventTypeId, 
       EventTypeOther = @EventTypeOther, 
       Loca.on = @Loca.on, 
       Comments = @Comments, 
       IsComplete = @IsComplete, 
       StartDate = @StartDate, 
       EndDate = @EndDate, 
       EventPrecision = @PrecisionType, 
       LastUpdateDate = GETDATE(), 
       LastUpdateUserId = @LastUpdatedBy, 
       SeriesID = @SeriesID, 
       Remarks = @Remarks--, 
       --OwnerId = @PgSentRefId --Use the ownerid as it never be used, saving crea.ng 1 more .eld 
   WHERE  
  ID = @EventId  

END 
ELSE 
BEGIN 
 -- if @intCount = 0, then the Record does not exist.  INSERT. 
 INSERT INTO [RSSOSS].[USMC].[Event] 
           (ID, 
            Organiza.onId, 
            EventSourceId, 
            EventTypeId, 
            EventTypeOther, 
            Loca.on, 
            Comments, 
            IsComplete, 
            StartDate, 
            EndDate, 
            EventPrecision, 
            LastUpdateDate, 
            LastUpdateUserId, 
            OwnerId, 
            SeriesID) 
     VALUES 
           (@EventId, 
            @Organiza.onId, 
            @EventSourceId, 
            @EventTypeId, 
            @EventTypeOther, 
            @Loca.on, 
            @Comments, 
            @IsComplete, 
            @StartDate, 
            @EndDate, 
            @PrecisionType, 
            GETDATE(), 
            @LastUpdatedBy, 
            @PgSentRefId, 
            @SeriesID) 

END 

END
