StoredProcedureSqlServerv2k5.body:
@Recrui.ngOrgID integer, @BeginDate date.me, @Ac.vi.esEntered bit OUTPUT AS
DECLARE @blnDataExists bit DECLARE @MonthCounter int DECLARE @FromDate date.me DECLARE @ToDate date.me
/* de.ne the month counter */ SET @MonthCounter = 3
/* loop through the previous three months and make sure there is RSS ac.vity entered for each month */ WHILE @MonthCounter >= 1 BEGIN /* get the begin and end dates of each month */ SET @FromDate = DATEADD(MONTH, -1 * @MonthCounter, @BeginDate) SET @ToDate = DATEADD(DAY, -1, DATEADD(MONTH, 1, @FromDate))
/* check for RSS ACTIVITY */ IF NOT EXISTS (SELECT RA.ORGANIZATION_ID FROM [dbo].[RSS_ACTIVITY] RA WITH (NOLOCK) INNER JOIN [dbo].[ORGANIZATION_MAP] OM ON RA.ORGANIZATION_ID = OM.CHILD_ORGANIZATION_ID WHERE RA.ACTIVITY_DATE BETWEEN @FromDate AND @ToDate AND OM.PARENT_ORGANIZATION_ID = @Recrui.ngOrgID) /* if there is no RSS ACTIVITY for the month set the DataExists .ag to false and exit the loop */ BEGIN SET @blnDataExists = 0 BREAK END ELSE BEGIN /* if all three months contain RSS ac.vity then set the .ag to True */ IF @MonthCounter = 1 SET @blnDataExists = 1 END
/* decrement the month counter and con.nue itera.ng through the loop */ SET @MonthCounter = @MonthCounter - 1 END
/* Set the return value. If ac.vi.es have been entered we return True. If ac.vi.es have not been entered we return false. */ SET @Ac.vi.esEntered = @blnDataExists
