StoredProcedureSqlServerv2k5.body:
@PersonId int AS
SELECT PAYMENT_SEQ_NUM, PAYMENT_AMOUNT, CONVERT(CHAR(11),FROM_DATE, 112) AS FROM_DATE, CONVERT(CHAR(11), TO_DATE, 112) AS TO_DATE, CASE WHEN PAYMENT_TYPE IN (99391,99393) THEN 'MCFAP' WHEN PAYMENT_TYPE IN (99392,99394) THEN 'MCTAP' END AS PAYMENT_TYPE, CUMULATIVE_AMOUNT, PAYMENT_DATE
FROM STIPEND WITH (NOLOCK) WHERE MARINE_CANDIDATE_ID = @PersonId ORDER BY FROM_DATE
