StoredProcedureSqlServerv2k5.body:
@Organiza.onID as integer AS
SELECT RP.RECRUITING_PERSON_ID,
CASE WHEN RP.RANK_CODE IS NOT NULL AND P.LAST_NAME IS NOT NULL AND P.FIRST_NAME IS NOT NULL AND P.LEGAL_MIDDLE_NAME IS NOT NULL THEN LTRIM(RTRIM(RP.RANK_CODE)) + ' ' + LTRIM(RTRIM(P.LAST_NAME)) + ' ' + LTRIM(RTRIM(P.FIRST_NAME)) + ' ' + LTRIM(RTRIM(P.LEGAL_MIDDLE_NAME)) WHEN RP.RANK_CODE IS NOT NULL AND P.LAST_NAME IS NOT NULL AND P.FIRST_NAME IS NOT NULL AND P.LEGAL_MIDDLE_NAME IS NULL THEN LTRIM(RTRIM(RP.RANK_CODE)) + ' ' + LTRIM(RTRIM(P.LAST_NAME)) + ' ' + LTRIM(RTRIM(P.FIRST_NAME)) WHEN RP.RANK_CODE IS NOT NULL AND P.LAST_NAME IS NOT NULL AND P.FIRST_NAME IS NULL THEN LTRIM(RTRIM(RP.RANK_CODE)) + ' ' + LTRIM(RTRIM(P.LAST_NAME)) ELSE LTRIM(RTRIM(RP.RANK_CODE)) END AS NAME FROM RECRUITING_PERSON RP WITH (NOLOCK), PERSON P WITH (NOLOCK) WHERE RP.ORGANIZATION_ID = @Organiza.onID AND RP.RECRUITING_PERSON_ID = P.PERSON_ID AND -- select only recruiters whose TOUR_END_DATE IS NULL RP.TOUR_END_DATE IS NULL ORDER BY NAME
